import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import React, { Component } from 'react';

import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import "./TabComponents.scss";
import Paper from "@material-ui/core/Paper";
import Modal from "@material-ui/core/Modal";
import Button from "@material-ui/core/Button";
import EditIcon from "@material-ui/icons/EditOutlined";
import DoneIcon from "@material-ui/icons/CheckCircleOutlined";
import AddIcon from "@material-ui/icons/AddOutlined";

class Notes extends Component {
    constructor(props) {
        super(props);
        this.state = {
            notes: [],
        };
    }

    componentWillMount() {
        //TODO: notes in descending order
        this.setState(() => {
            let notes = Object.keys(this.props.user_notes).map((noteID) => {
                return this.props.user_notes[noteID];
            });
            return {
                notes: notes,
            }
        });
    }

    render() {
        console.log(this.state.notes);
        let iconStyle = {
            fontSize: "16px",
            align: "right"
        }
        let numericDateString = (date) => {
            let DateObject = new Date(date),
                numericOptions = {
                    month: "numeric",
                    day: "numeric",
                    hour: "2-digit",
                    minute: "2-digit"
                };
            return DateObject.toLocaleTimeString("en-US", numericOptions);
        };
        return (<Grid item md={12}>
            <Grid container spacing={16}>
                <Grid item md={3}>
                    <div className="addNote"
                    onClick={() => {
                        <>
                        <Modal
                            aria-labelledby="simple-modal-title"
                            aria-describedby="simple-modal-description"
                            open={this.state.alert}
                            onClick={this.hideWarning.bind(this)}>
                            <div className="exit-popup">
                                <Typography variant="h6" id="modal-title">
                                    {this.props.alertMessage || "Are you sure you want to leave unsaved changes?"}
                                </Typography>
                                <Button
                                    onClick={(e) => {
                                        e.preventDefault();
                                        this.denyAction.bind(this)(this.props.denyAction)
                                    }}
                                    color="secondary"
                                    className="button secondary">
                                    {this.props.alertDenyText || "No, leave me here"}
                                </Button>
                                <Button
                                    onClick={(e) => {
                                        e.preventDefault();
                                        this.confirmAction.bind(this)(this.props.confirmAction)
                                    }}
                                    color="primary"
                                    className="button primary">
                                    {this.props.alertConfirmText || "Yes, take me back"}
                                </Button>
                            </div>
                        </Modal>
                    </>
                    }}
                    style={{
                        "cursor": "pointer",
                    }}>
                        <Typography className="center">
                            <AddIcon/>
                            <br/>
                            Add Note
                        </Typography>
                    </div>
                </Grid>
                {this.state.notes.map((note, i) => {
                    return <Grid item xs={3} key={i}>
                        <Paper className={"note"}>
                            <Typography className={"body"} align={'left'}>
                                {note.body}
                            </Typography>
                            <Typography className={"date"} style={{ fontWeight: "500" }}>
                                {numericDateString(note.timestamp).replace(",", "・")}
                            </Typography>
                            <div className={"actions"}>
                                <EditIcon style={iconStyle} />
                                <DoneIcon style={iconStyle} />
                            </div>
                        </Paper>
                    </Grid>
                })}
            </Grid>
        </Grid>)
    }

}

Notes.propTypes = {
    user_notes: PropTypes.object,
};

function mapStateToProps(state) {
    return {
    };
}

function mapDispatchToProps(dispatch) {
    return {};
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Notes);
